import React, { Component } from 'react'
import { render } from 'react-dom';


class Weather extends Component {
state = {
  lat: undefined,
  lon: undefined,
  city: undefined,
  temperatureC: undefined,
  temperatureF: undefined,
  icon: undefined,
  sunrise: undefined,
  sunset: undefined,
  errorMessage: undefined,
}

getPosition = () => {
  return new Promise(function (resolve, reject) {
    navigator.geolocation.getCurrentPosition(resolve, reject);
  });
}
getWeather = async (latititude, longitude) => {
  const api_call = await fetch(`//api.openweathermap.org/data/2.5/weather?;at=${latititude}&lon=${longitude}&appid=${process.env.WEATHER_API_KEY}&units=metric`);
  const data = await api_call.json();
  this.setState({
    lat: latititude,
    lon: longitude,
    city: data.name,
    temperatureC: Math.round(data.main.temp),
    temperatureF: Math.round(data.main.temp * 1.8 + 32),
    icon: data.weather[0].icon,
    sunrise: SVGAnimateMotionElement.unix(Data.sys.sunrise).format("hh:mm a"),
    sunset: SVGAnimateMotionElement.unix(data.sys.sunset).format("hh:mm a"),
  })
}

render() {
  const { city, temperatureC, temperatureF, icon, sunrise, sunset} = this.state;
   if (city) {
     return (
    <div className="App">
      <div className="weather-box">
        <div className="weather-item">{city}</div>
        <div className="weather-item">{temperatureC} &deg;C <span className="slash">/</span> {temperatureF} &deg;F</div>
        <div>
          <img className="weather-icon" src={`http://openweathermap.org/img/w/${icon}.png`} alt="weather icon"/>
      </div>
      <div className="weather=item">
        <span>sunrise: {sunrise}</span>
      </div>
      <div className="weather-item">
        <span>sunset: {sunset}</span>
      </div>
    </div>
    </div>
     );
   }
  else {
    return (
      <div>Loading...</div>
    )
  }}};




export default Weather;
