import React, { useState, useEffect } from 'react';


function Weather(props) {
  const [localweather, setLocalweather] = useState('');
  const [query, setQuery] = useState('');
  const [temp, setTemp] = useState(0);
  const [unit, setUnit] = useState('');
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

   

useEffect(() => {
  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition((position) => {
      const lon = position.coords.longitude;
      const lat = position.coords.latitude;
      console.log(lat);
      console.log(lon);
      const API_KEY = '3b66b0c3c2f2e8c4e63630359492b5bf';
      fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&units=metric&appid=${API_KEY}`)
      .then(res => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])
}})

if (error) {
  return <div>Error: {error.message}</div>;
} else if (!isLoaded) {
  return <div>Loading...</div>;
} else {
  return (
    <ul>
      {main.temp(item => (
        <li key={main.temp}>
          {item.name} {item.price}
        </li>
      ))}
    </ul>
  );
}


const oppositeUnit = unit === "C" ? "F" : "C";

 

const convert = () => {
  if (unit === "C") {
    temp(({temp} - 273.15).toFixed(0));
    // const setTemp = (Math.round(temp));
    setUnit(oppositeUnit);
  }


  if (unit === "F") {
    temp((({temp} - 273.15) * 1.8 + 32).toFixed(0));
    // setTemp=(Math.round(temp));
    setUnit(oppositeUnit);
  }
};


    return (
      <div>

        <p>Temperature!{temp}{unit}</p>
        <button onClick={convert()}>Convert to {oppositeUnit}</button>
        <span className="value">{temp}{unit}</span>

      </div>

    )
}

  export default Weather;
