import React, { Component } from 'react'




class Weather extends Component {
state = {
  lat: undefined,
  lon: undefined,
  city: undefined,
  temperatureC: undefined,
  temperatureF: undefined,
  icon: undefined,
  errorMessage: undefined,
}

getPosition = () => {
  return new Promise(function (resolve, reject) {
    navigator.geolocation.getCurrentPosition(resolve, reject);
  });
}
getWeather = async (latitude, longitude) => {
  let WEATHER_API_KEY= '4ffc9c1f1cabd61e0d206518a5dc13cd'
  const api_call = await fetch(`//api.openweathermap.org/data/2.5/weather?;at=${latitude}&lon=${longitude}&appid=${WEATHER_API_KEY}&units=metric`);
  const data = await api_call.json();
 

  this.setState({
    lat: latitude,
    lon: longitude,
    city: data.name,
    temperatureC: Math.round(data.main.temp),
      temperatureF: Math.round(data.main.temp * 1.8 + 32),
    icon: data.weather[0].icon
  })
}

componentDidMount() {
  this.getPosition()
  .then((position) => {
    this.getWeather(position.coords.latitude, position.coords.longitude)
  })
  .catch((err) => {
    this.setState({ errorMessage: err.message });
  });

  this.timerID = setInterval(
    () => 
    this.getWeather(this.state.lat, this.state.lon),
    60000
  );
}

componentWillUnmount() {
  clearInterval(this.timerID);
}

render() {
  const { city, temperatureC, temperatureF, icon} = this.state;
   if (city) {
     return (
    <div className="App">
      <div className="weather-box">
        <div className="weather-item">{city}</div>
        <div className="weather-item">{temperatureC} &deg;C <span className="slash">/</span> {temperatureF} &deg;F</div>
        <div>
          <img className="weather-icon" src={`http://openweathermap.org/img/w/${icon}.png`} alt="weather icon"/>
      </div>
    </div>
    </div>
     );
   }
  else {
    return (
      <div>Loading...</div>
    )
  }}}




export default Weather;
